import React, { Component } from 'react';
import './App.css';
import Movie from './movie';

class App extends Component {
	//Render : componentWillMount() -> render() -> componentDidMount()
	//Update componentWillReceiveProps() -> shouldComponentUpdate() -> componentWillUpdate() -> render() -> componentDidUpdate()

	state = {
		greeting : 'Hello',
		movies : [
			{
				title : "Matrix",
				poster :  "https://upload.wikimedia.org/wikipedia/en/thumb/9/9a/The_Matrix_soundtrack_cover.jpg/220px-The_Matrix_soundtrack_cover.jpg"
			},
			{
				title : "Inception",
				poster :  "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_UX182_CR0,0,182,268_AL_.jpg"
			},
			{
				title : "Old Boy",
				poster :  "https://upload.wikimedia.org/wikipedia/en/thumb/6/67/Oldboykoreanposter.jpg/220px-Oldboykoreanposter.jpg"
			},
			{
				title : "Star Wars",
				poster :  "https://www.blendswap.com/files/images/2012/04/blend_42863/vantlers_sc_medium_a416d19ab0fd1164184e1e6014caab7cb04a8360.jpg"
			}

		]
	}

	componentDidMount(){
		//this.state.greeting : 'Hello again' //직접적으로 변경하면 Render가 정상 작동을 안함
		setTimeout( () => {
			this.setState({ //state를 변경하려면 setState를 사용해서 변경해야 한다
				greeting : 'Hello again',
				movies : [
					...this.state.movies,
					{
						title : "Hahaha",
						poster :  "https://upload.wikimedia.org/wikipedia/en/thumb/9/9a/The_Matrix_soundtrack_cover.jpg/220px-The_Matrix_soundtrack_cover.jpg"
					}
				]
			})
		}, 5000)
	}

	render() {
		return (
			<div className="App">
				{this.state.greeting}
				{this.state.movies.map( (movie, index) =>{
					return <Movie title={movie.title} poster={movie.poster} key={index} />
				})}
			</div>
		);
	}
}

export default App;
