import React, { Component } from 'react';
import './App.css';
import Movie from './movie';

const MovieTitle = [
	"Matrix",
	"Inception",
	"Old Boy",
	"Star Wars"
]

const MovieImage = [
	"https://upload.wikimedia.org/wikipedia/en/thumb/9/9a/The_Matrix_soundtrack_cover.jpg/220px-The_Matrix_soundtrack_cover.jpg",
	"https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_UX182_CR0,0,182,268_AL_.jpg",
	"https://upload.wikimedia.org/wikipedia/en/thumb/6/67/Oldboykoreanposter.jpg/220px-Oldboykoreanposter.jpg",
	"https://www.blendswap.com/files/images/2012/04/blend_42863/vantlers_sc_medium_a416d19ab0fd1164184e1e6014caab7cb04a8360.jpg"
]

class App extends Component {
render() {
	return (
		<div className="App">
			<Movie title={MovieTitle[0]} poster={MovieImage[0]} />
			<Movie title={MovieTitle[1]} poster={MovieImage[1]} />
			<Movie title={MovieTitle[2]} poster={MovieImage[2]} />
			<Movie title={MovieTitle[3]} poster={MovieImage[3]} />
		</div>
	);
}
}

export default App;
